INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2717, 100003, 0, NULL, 61, '网约车&代驾CPA+CPS活动', '每日领网约车&代驾大额红包', 'https://img.jutuike.com/taokeout/banner/didi_dache_daijia_banner.png', 'https://img.jutuike.com/taokeout/icon/didi_dc_icon.png', '', '最高15元', '<p>1.按照末次点击归因，用户进入推广链接后产生有效点击（必须是领券点击）即可进行跟单，产生的订单归属于有效期内最后一次触达用户的媒体，<span style=\"color:#d0021b\">点击有效期有7天。</span><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; color: rgb(0, 0, 0);\">用户每次点击领券可追踪订单1次，每个自然日最多追踪订单2次。<br/></span></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; font-family: inherit;\"><br/></span></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; font-family: inherit;\">2.若用户同时领取常规网约车活动与大额券活动优惠券，并且使用大额券活动优惠券完单，该笔订单将不计算常规网约车CPS活动返佣，同时常规网约车CPS活动绑定关系将失效。&nbsp;</span><br/></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; font-family: inherit;\"><br/></span></p><p>3.邀请人参加滴滴平台的多个新客CPA活动，在与用户首次建立绑定关系后，不能重复邀请同一新用户参与其他活动，同一新用户仅与首次受邀的活动建立绑定关系，邀请人仅可领取该活动奖励，重复邀请无效。<span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; font-family: inherit;\"><br/></span></p><p><span style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline;\">注意：</span>为了更好的规范合作伙伴的推广方式以及落实禁止在车内场景进行推广的政策，从2022年4月5日起，对于命中车内场景的用户，即日起120天（包含触发当天以及第120天）内任何网约车订单将不进行渠道返佣。</p><p><br/></p>', '<p>网约车CPA：12元/人</p><p>网约车CPS：2.5%</p><p>代驾CPA：15元/人</p>', '次月20号', '2024-04-11', '2025-12-31', 1744732745, 1744732745);
