INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2728, 100003, 0, NULL, 80, '饿了么赚现金活动', '饿了么天天领红包', 'https://img.alicdn.com/imgextra/i3/6000000006258/O1CN01BTbhKi1w6DRKAwuum_!!6000000006258-0-o2oad.jpg', 'https://img.jutuike.com/taokeout/icon/elmkb_icon.png', 'https://img.jutuike.com/taokeout/poster/new/10690poster.jpg', '佣金最高60元', '<p>天天赚现金现</p><p>CPA奖励最高60元</p><p>用户最高领20元红包</p><p><br/></p>', '<p><span style=\"text-wrap: nowrap;\">一、活动介绍</span></p><p><span style=\"text-wrap: nowrap;\">1、活动时间内，推广者可以以海报、饿口令等给好友，邀请成功后，邀请者将获得根据本规则获得相应现金奖励；</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">2、邀请者通过分享链接、海报或饿口口令邀请好友领取红包，好友在领取当日使用“[天天赚现金]”标识的红包并完成实付金额超过7元的订单（到店自取订单除外），且无退款，视为邀请成功（被邀请者订单完成时间以订单送达时间判定），邀请者可有机会获得现金奖励。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">二、奖励发放、使用</span></p><p><span style=\"text-wrap: nowrap;\">1.&nbsp; &nbsp;邀请者每成功邀请一名饿了么用户，可获得随机现金奖励，多邀多得。其中，每成功邀请一名饿了么新用户，可能获得的现金奖励金额范围为【0.01-60.0】元；每成功邀请一名饿了么老用户，可能获得的现金奖励金额范围为【0.01-30.0】元。现金奖励金额随机，具体以活动页面展示为准。但受邀请的用户下单实付不足7元，则现金奖励为0.01元。在法律法规许可的范围内，根据活动开展情况，现金奖励存在不定期进行调整可能，如有调整会在本规则提前公示。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">三、被邀请者红包</span></p><p><span style=\"text-wrap: nowrap;\">1. 被邀请好友通过分享的链接、二维码，在登录或首次注册后，可获得一个吃货联盟红包。该红包可用于在线抵扣订单，该红包可到“饿了么APP或支付宝饿了么小程序 – 我的 – 红包卡券”查看。吃货联盟红包仅用于领取用户在饿了么平台订购外卖，限吃货联盟商家可用，不可转让、出售，具体使用规则和有效期详见“饿了么APP或支付宝饿了么小程序 – 我的 – 红包卡券”。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">2. 同一用户作为被邀请者每天通过「天天赚现金」活动最多可以领取1个吃货联盟红包，红包的形式与额度随机，数量有限，领完为止，并非100%领到，以实际展示为准。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">四、活动其他说明</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">1、饿了么新用户是指设备号、手机号、饿了么账户（包含相关第三方平台账户，如场景适用）及支付账户均未在饿了么外卖任何平台上下过单的用户。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">2、饿了么老用户是指除饿了么新用户之外的饿了么注册用户。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">3、 因本活动参与者数量较多，活动页面的数据统计和展示可能会有延迟，敬请谅解。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">4、 在参与活动的过程中，若推广者出现违规违法行为（如作弊、虚假交易、虚假夸大、暴力营销等），所涉订单将被平台判定为风控订单，联盟平台有权取消该推广者参与活动的资格、取消其获得对应现金奖励的资格，并有权收回全部活动奖励（含已使用的及未使用的），必要时追究法律责任。&nbsp;&nbsp;</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">5、 活动期间如出现不可抗力或情势变更的情况（包括但不限于重大灾害事件、活动受政府机关指令需要停止举办或调整的、活动遭受严重网络攻击或因系统故障需要暂停举办的），联盟平台可依相关法律法规的规定主张免责。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">6、 在法律法规许可范围内，联盟平台可以根据本活动的实际举办情况对活动规则进行变动或调整，相关变动或调整将公布在活动页面上，公布后依法生效。</span></p><p><span style=\"text-wrap: nowrap;\"><br/></span></p><p><span style=\"text-wrap: nowrap;\">7、 本活动与其他营销活动互斥。</span></p><p><br/></p>', '次月20号', '2022-07-17', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2729, 100003, 0, NULL, 83, '飞猪酒店特惠', '飞猪酒店 热门精选、天天特惠', 'https://img.jutuike.com/taokeout/banner/feizhu_hotal_banner.png?v=1', 'https://img.jutuike.com/taokeout/icon/feizhu_hotel_jtk.png', '', '平均3%', '<p>热门精选飞猪酒店，天天享受特惠酒店</p>', '<p>通过小程序推广连接下单可得平均3%佣金</p><p>代理商非官方售卖房型及战略合作酒店房型无奖励</p>', '次月20号', '2022-07-11', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2730, 100003, 0, NULL, 86, '飞猪民宿特惠推广', '底价抢先订，领券下单低至3折起，50万套城市核心商圈酒店公寓/居民住宅，一线二线城市覆盖范围广', 'https://img.jutuike.com/taokeout/banner/feizhu_minshu_banner.jpg', 'https://img.jutuike.com/taokeout/icon/feizhu_hotel_jtk.png', 'https://img.jutuike.com/taokeout/poster/new/feizhu_minshu_poster.jpg', '佣金3%', '<p>底价抢先订，领券下单低至3折起，50万套城市核心商圈酒店公寓/居民住宅，一线二线城市覆盖范围广</p>', '<p>通过推广链接进入下单可的预计佣金3%返佣</p>', '次月20号', '2024-06-14', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2731, 100003, 0, NULL, 87, '同程打车/顺风车CPS+CPA拉新', '用车业务现覆盖全国360+座城市，为用户提供全面便捷出行服务', 'https://img.jutuike.com/taokeout/banner/tcyl_banner_jtk.png', 'https://img.jutuike.com/taokeout/icon/tcyl_icon_jtk.png?v=2', 'https://img.jutuike.com/taokeout/poster/new/tcyl_poster.png?v=3', 'CPA5、10元+3%', '<p style=\"line-height: 1.75em;\"><span style=\"text-align: center; font-size: 14px;\">同程打车是同程艺龙旗下聚合用车服务平台，目前已经接入了众多出行服务平台，在全国范围内提供出租车、经济、舒适、商务、豪华等多种车型以及即时打车、预约、接送机等多种场景服务。</span><span style=\"font-size: 14px; text-align: center;\">（包含高德、曹操、首汽、阳光，T3，神州、享道、滴滴等50多家供应商资源），用车业务现覆盖全国400座城市，为用户提供全面便捷出行服务。</span></p><p style=\"line-height: 1.75em;\"><strong><span style=\"font-size: 14px; text-align: center;\"></span></strong></p><p><br/></p><p><strong><span style=\"font-size: 14px;\">用户权益说明</span></strong></p><p><span style=\"font-size: 14px;\"></span></p><p style=\"margin-top: 0;margin-bottom: 0;font-family: 宋体;font-size: 16px;white-space: normal\"><span style=\"font-family: 微软雅黑; font-size: 14px;\"></span></p><p>打车券——</p><p>新客券6元打车红包（满20）</p><p>老客券2元（10）</p><p>顺风车券——</p><p>新客券8元顺风车券（满50）、12元（满80） 顺风车接送机通用券</p><p>老客97折cap15(满50）</p>', '<p style=\"font-size: medium; white-space: normal; font-family: &quot;PingFang SC&quot;; line-height: 1.75em;\"><strong><span style=\"font-family: DengXian; background: white; color: rgb(51, 51, 51); font-size: 14px;\">CPS结算：</span></strong><span style=\"font-family: DengXian; background: white; color: rgb(51, 51, 51); font-size: 14px;\">新用户及老用户通过淘客渠道领取到优惠券，核销优惠券完成的有效打车订单（新用户除首单外的订单），按订单实付金额*佣金率3%结算。</span></p><p style=\"font-size: medium; white-space: normal; font-family: &quot;PingFang SC&quot;; line-height: 1.75em;\"><strong><span style=\"background: white; font-family: arial, helvetica, sans-serif; font-size: 14px;\">&nbsp;</span><span style=\"background: white; font-size: 14px; font-family: DengXian; color: rgb(51, 51, 51);\">CPA新客结算：</span></strong><span style=\"background-color: white; font-family: arial, helvetica, sans-serif; font-size: 14px;\">&nbsp;同程</span><span style=\"background: white; font-size: 14px; font-family: DengXian; color: rgb(51, 51, 51);\">打车新用户，通过渠道领取到优惠券，并核销优惠券完成打车首单，按每个有效</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"background: white; font-size: 14px; font-family: DengXian;\">（打车<strong style=\"color: rgb(255, 0, 0); font-family: &quot;PingFang SC&quot;; font-size: medium; white-space: normal;\"><span style=\"background: white; font-size: 14px; font-family: DengXian;\">新用户</span></strong>：5元、顺风车新用户：10</span><strong style=\"font-family: arial, helvetica, sans-serif; font-size: 14px;\"><span style=\"font-family: DengXian; background: white;\">元）</span></strong></strong></span><span style=\"background: white; font-size: 14px; font-family: DengXian; color: rgb(51, 51, 51);\">奖励金结算。（仅支持打车首单，无首单或者取消订单无效；每位用户仅补贴一次）</span></p><p><br/></p>', '次月20号', '2022-08-22', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2732, 100003, 0, NULL, 89, '美团赏金红包', '限时抢商家优惠券，可与商家满减、折扣同时使用', 'https://img.jutuike.com/taokeout/banner/meituan_shangjin_banner.png', 'https://img.jutuike.com/taokeout/icon/meituan_shangjin_icon.png', 'https://img.jutuike.com/taokeout/poster/new/meituan_shangjin_poster.png', '比例4%', '<p>用户活动参与规则：</p><p>每位用户每日可参与一次活动获得红包/商家优惠券，该红包/商家优惠券可与商家满减、折扣同时使用。</p><p>1.用户在红包专区可领取限定大额外卖通用红包，在指定优质商家参与交易可使用该红包。</p><p><br/></p><p>2.用户在领券专区可领取优质外卖商家券。商家券活动分为两种形式，用户可同享：</p><p>（1）【每日优惠】栏目，用户单日可一键领取多张推荐外卖商家券，并在指定优质商家完成核销；</p><p>（2）【福利自选】栏目，用户可通过浏览、搜索等形式，选择喜欢的商家领取优惠券。</p><p><br/></p><p>用户用券范围更广泛，领券更精准，转化率更高！</p><p><br/></p><p>媒体宣传建议：</p><p>根据对美团外卖用户的研究，多数用户在点餐前已有较明确的目标品类或商家。因此，长期我们的定位是用户能够在最想下单的商家享受到市面上最高的优惠。利益点：</p><p><br/></p><p>A、转化更精准：引入搜索/筛选，用户吃哪家、搜哪家、领哪家；</p><p><br/></p><p>B、活动更丰富：包括但不限于通用红包、商家券、津贴、返佣、签到赢奖励等等；</p><p><br/></p><p>C、优惠能叠加：多项福利与平台优惠可叠加使用，用户更满意。</p><p><br/></p><p>创意上，媒体合作商可选择以上单个/多个利益点向用户宣导，进行效果测试；找准您的用户最感兴趣的利益点作为创意来展示。</p><p><br/></p><p>资源位上，媒体合作商可选择更优质资源位测试我们的推广活动效果，让您的用户能更主动、更准确选择商家，促成转化。</p><p><br/></p><p>推广方式上，媒体合作商可通过活动包装推文、社群宣导、定时点餐提醒等方式刺激用户领券下单。当然，也鼓励您创新更好的模式！</p><p><br style=\"white-space: normal;\"/></p><p><br/></p>', '<p>符合以下规则的订单，媒体可根据该订单实际成交金额获取佣金，活动订单判定规则如下：</p><p>1.用户在推广页内领取红包/优惠券，在优惠券有效期内用优惠券完成交易；</p><p><br/></p><p>2.用户在推广页内领取红包/优惠券，且24小时内在领券商家完成交易；</p><p><br/></p><p>3.用户点击推广页内商家，且24小时内在该商家完成交易；</p><p><br/></p><p>4.用户在推广页内领取红包/优惠券或点击推广页内商家，且24小时内在推广页内商家完成交易。</p>', '次月20号', '2023-01-01', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2733, 100003, 0, NULL, 91, '飞猪火车票CPS', '飞猪旅行-天天特惠 飞猪火车票安心购票、便捷出行', 'https://img.jutuike.com/taokeout/banner/feizhu_train_banner_jtk.png', 'https://img.jutuike.com/taokeout/icon/feizhu_train_jtk.png', '', '3.5元/单', '<p>佣金政策：火车票3.5元/单<span style=\"color: rgb(255, 0, 0);\">(需通过小程序下单)</span></p><p>有效期：2024/12/31</p><p>推广文案：飞猪火车票安心购票、便捷出行，搭配券包更划算，一单回本</p><p>归因逻辑：用户点击专属链接后，7天内有效成交计入分佣业绩，仅返佣带辅营产品的订单（辅营产品不包含如下类型：加速包/省钱卡/夜间购票/夜间抢票）</p>', '<p>用户点击专属链接后，7天内有效成交计入分佣业绩，仅返佣带辅营产品的订单（辅营产品不包含如下类型：加速包/省钱卡/夜间购票/夜间抢票）</p>', '次月20号', '2022-09-10', '2025-12-31', 1744732745, 1744732745);
