INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2755, 100003, 0, NULL, 117, '超值券包&外卖&酒店聚合页', '超值券包囤美味！大牌爆品+超低价格+优惠期长！', 'https://s3plus.meituan.net/v1/mss_623ff9bb9f0f4bb0a98733e491a41533/union/42ec9e596148ee86a554c7c5afe895bb.jpg', 'https://img.jutuike.com/taokeout/icon/meituan_czqb_icon.png?v=2', 'https://img.jutuike.com/jutuike/meituan_coupon/meituan_coupon_221.png', '比例10%-20%', '<p>根据商家的定价策略，分用户、分地域券包价格可能会有差异，以系统展示页面为准。</p>', '<p style=\"white-space: normal;\">券包：</p><p style=\"white-space: normal;\">CPS 归因： 订单跟单规则为用户进入推广链接后产生有效点击即可进行跟单，产生的券包 订单归属于用户发生交易行为前最后一次触达用户的媒体。</p><p style=\"white-space: normal;\"><br/></p><p style=\"white-space: normal;\">CPS佣金10%每周五20%：若用户在推广页购买超值券包，且在有效期内使用券包中的代金券完成交易， 则该笔佣金为该张券分摊的实付金额的 10%每周五20%</p><p style=\"white-space: normal;\">酒店：</p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">归因逻辑：按照用户点击跟踪，针对用户点击推广链接后产生的订单结算。&nbsp;</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">归因时间：H5 页面为 24 小时，唤起页面为 0.5 小时，微信小程序为 0.5 小时。&nbsp;</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px; color: rgb(255, 0, 0);\">特别说明：用户点击推广链接后，如果未下单且离开该页面，则不计算佣金。</span></p><p>外卖：</p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">订单跟单规则为用户进入推广链接后产生有效点击即可进行跟单，产生的订单归属于点击 有效期内最后一次触达用户的媒体，点击有效期最长为 7 天；若订单使用了从推广链接领 取的红包，且订单不存在有效期内的点击，订单归为推广红包的媒体。</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\"></span></p><p style=\"white-space: normal;\"><strong><span style=\"font-size: 14px; color: rgb(255, 0, 0);\">2022年3月1日新规如下：</span></strong></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">1、若用户在推广页领取到平台红包或商家红包，且在有效期内使用该红包完成交易，则佣金为该笔订单实付价的3%。</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">2、若用户点击进入推广页上的商家，且24小时内在该商家完成交易，则佣金为该笔订单实付价的3%。</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">3、<span style=\"color: rgb(255, 0, 0);\">若用户在推广页领取到平台红包或商家红包，且24小时内在未点击过的商家完成交易，但未使用领取到的红包，则佣金为该笔订单实付价的0.1%</span>。</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">上述规则对2022年3月1日0时后产生的订单生效，每笔订单仅结算一次，优先级为2＞1＞3。</span></p><p style=\"white-space: normal; line-height: 1.75em;\"><span style=\"font-size: 14px;\">另外，高佣大促活动（外卖3%起）、高佣商家池（外卖6%起）等高佣订单以实际结算时的佣金比例为准。</span></p>', '次月10号', '2023-04-19', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2756, 100003, 0, NULL, 118, '外卖社群加粉CPA', '外卖社群加粉CPA', 'https://s3plus.meituan.net/v1/mss_623ff9bb9f0f4bb0a98733e491a41533/union/910fa09a310aadd229e90e4ad872d86e.png', 'https://img.jutuike.com/taokeout/icon/meituan.png', '', 'CPA 7元', '<p>外卖社群加粉CPA</p>', '<p>结算对象：通过外卖推广页的二维码进入美团外卖企业矩阵微信群/企业矩阵公\r\n众号，且首次在群公告或群发消息链接或公众号中成功领券的美团外卖用户，\r\n且在领券后 24 小时内在美团外卖产生有效订单；</p><p>每个美团外卖用户仅结算一\r\n次。&nbsp;</p><p>结算金额：7 元/人&nbsp;</p>', '次月10号', '2023-04-19', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2757, 100003, 0, NULL, 119, '美团外卖私域推广活动', '关注公众号得80元新人券包', 'https://s3plus.meituan.net/v1/mss_623ff9bb9f0f4bb0a98733e491a41533/union/910fa09a310aadd229e90e4ad872d86e.png', 'https://img.jutuike.com/taokeout/icon/meituan.png', '', '预估8元', '<p><span style=\"color: rgba(0, 0, 0, 0.84); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Heiti SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; text-align: center; background-color: rgb(255, 255, 255);\">关注公众号得80元新人券包，T+3出结算数据，按照首次关注公众号用户口径结算</span></p>', '<p>（1）活动期间进入美团私域-企微个人号的美团私域纯新用户结算8元/人；&nbsp;</p><p>（2）当日新增删除率≤50%，当日新增用户领券率≥60%，不满足的天数不予结算</p><p><span style=\"color: rgba(0, 0, 0, 0.84); font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, &quot;PingFang SC&quot;, &quot;Heiti SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, sans-serif; font-size: 14px; text-align: center; text-wrap: wrap; background-color: rgb(255, 255, 255);\">特别说明：首次进入美团外卖私域的用户（若被风控识别存在违规推广行为，当日用户均不结算）</span></p><p><br/></p>', '次月10号', '2023-12-01', '2025-12-31', 1744732745, 1744732745);
INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2758, 100003, 0, NULL, 120, '飞猪酒店机票火车票门票聚合活动', '机票酒店火车票门票轻松预订 出行优惠天天享', 'https://img.jutuike.com/taokeout/banner/feizhu_cps_banner.png', 'https://img.jutuike.com/taokeout/icon/feizhu_hotel_jtk.png', '', '实际产品为准', '<p>机票酒店火车票轻松预订 出行优惠天天享</p>', '<p>酒店佣金：</p><p>1、有效酒店类商品库均以小程序售卖中的酒店为准；</p><p>2、购买有效酒店类商品的订单，按照有效订单金额的一定比例进行奖励的发放；奖励佣金比率为交易订单费用的3%；</p><p>3、平台判定为虚假交易、违规交易的订单不发放奖励。最终判定以飞猪平台为准；</p><p>4、用户点击推广者专属链接后，14天内产生在多端包括飞猪小程序/APP，淘宝APP、支付宝小程序（不包括高德）等成交的有效订单均计入业绩；</p><p>机票佣金：</p><p>比例在0.2~1.5%<br/></p><p>*机票核销交易额指按月度期间用户通过小程序推广购买飞猪国内机票，且结算时间前订单状态不是维权创建和维权成功的、可结算佣金的订单所产生的累计实际成交金额（机场/燃油税费、保险费及消费者下单时所使用的店铺红包、优惠券、购物津贴等将不计入实际成交金额；订单不包含“春秋”及“九元”航空公司订单）</p><p>5、代理商非官方售卖房型及战略合作酒店房型无奖励</p><p>火车票佣金：</p><p>一、政策周期： 即日起 - 2023年5月4日</p><p>二、具体规则：</p><p>1、政策表中的X指：保险订单或者是代购订单，以保险/代购订单的订单数按以上阶梯执行；对外宣传时，不得使用保险订单字样，统一用：仅返佣带辅营产品的订单（辅营产品不包含如下类型：加速包/省钱卡/夜间购票/夜间抢票）&nbsp;</p><p>2、结算政策：推广者在单个自然月内完成的含保险订单+代购订单的实际出行订单笔数，如果1笔订单中购买了2张票，其中既有保险单又有代购单，结算只算1笔。</p><p>3、渠道相关：</p><p>&nbsp;- 渠道范围：高德 不计入返佣；个人渠道仅按照3.5元/单结算，不设置阶梯档位；</p><p>&nbsp;- 渠道结算：我们只和一级推广者结算，即由渠道运营配置了渠道id的才算我们的结算方，如A推广者给到他自己的资源A1、A2、A3、A4售卖，最终我们都只认A的订单数。</p><p>&nbsp;- 渠道结算限制：同一推广者渠道，同一天内，同一消费者用户的cps归因订单，至多结算10笔；第11笔，不结算；</p><p>4、订单金额限制：火车票订单金额小于10元的不结算；</p><p>5、退票政策：以实际火车票的线上订单状态校验是否可结算，如出现线下退票的情况，暂时忽略，认为可以给推广者返佣</p><p>门票佣金 1%</p>', '次月20号', '2023-04-23', '2025-12-31', 1744732745, 1744732745);
