INSERT INTO `saas_cps_act` (`id`, `site_id`, `type`, `cid`, `act_id`, `act_name`, `desc`, `img`, `icon`, `poster`, `commission_rate`, `introduce`, `attribution_explain`, `settlement_time`, `start_date`, `end_date`, `create_time`, `update_time`) VALUES (2778, 100003, 0, NULL, 151, '去哪儿网赚现金', '注册领取之后，邀请返佣在工作日期间次日24点前，会直接升级到1.5元，新客下单最高8.5元，老客最高5元(升级前也可以正常推广，邀请返佣是1元，下单是8元)\r\n温馨提示：自己的推广海报，要从去哪儿网页面分享出去，不要直接分享聚推客的推广海报\r\n', 'https://img.jutuike.com/taokeout/banner/qunar_banner.png', 'https://img.jutuike.com/taokeout/banner/qunar_logo.png?v=1', '', '现金10元', '<p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">1、领券返现：将本活动分享给新客用户且该用户领券成功的，分享人即可获得0.5-2元收益；</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">2、下单返现：分享人将本活动分享给其他用户，若被分享的用户下单且满足以下条件，分享人可获得收益:</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">①去哪儿小程序新客且在被分享后7天内在本活动页完成登录绑定并在小程序内支付完成了酒店订单(订单实际支付金额需大于10元)，该订单履行完毕后分享人可获得9.5元下单返佣(仅限新客支付的第一单);</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">②)去哪儿小程序老客在被分享后24小时内在本活动页完成登录绑定并在小程序内支付完成了酒店订单，该订单履行完毕后分享人可获得老客用户实际支付金额的3%作为收益(24小时内支付完成酒店订单后，24小时后可再次绑定账户，满足条件后分享人仍可获得收益);</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">③)分享人与被分享人应为成功绑定的关系，且成功绑定后被分享人的信息会展示在活动页-我的收入板块；</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">(4)在本活动中，新客在点击进入活动页7天后即为老客(无论新客在7天内是否下单)，在活期限内可按照老客下单返现的活动要求参与;</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">(5)返现量级限制:下单返现的绑定量级无限制，但每人每天最多可以分享其他领券返现人数为200人;如需增加领券返现人数上限，需要联系商务人员扩容!</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">(6)绑定周期:小程序新客绑定周期为7天，老客绑定周期为24H。点击分享者链接且进行登录操作即为有效绑定被绑定账号在绑定周期无法解绑;下单返现以账号绑定为准，与是否领券无关;</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">(7)被分享人点击券包领取按钮即可领取券包，同一用户当天只能从本活动页面领取成功1次酒店券包，禁止多号领取。</p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">(8)用户理解并确认:领取请求和领取结果可能因网络信息传输延迟导致延期或者错误送达，此种情况并不属于活动方欺诈或违约，实际领取情况以活动主办方记录为准；</p><p><br/></p><p style=\"margin-top: 10px; margin-bottom: 10px; line-height: 1.5em;\">温馨提示：自己的推广海报，要从去哪儿网页面分享出去，不要直接分享聚推客的推广海报</p><p><br/></p>', '<p>通过聚推客联盟认证成渠道推广身份后，获取自己的推广海报，进行推广即可获取现金奖励<br/></p><p><span style=\"color: rgb(255, 0, 0);\">非常重要：不要直接分享聚推客推广海报，需要先通过聚推客推广海报认证推广员后从去哪儿网页面分享自己的推广海报</span></p><p><span style=\"color: rgb(255, 0, 0);\"><br/></span></p><p><span style=\"color: rgb(255, 0, 0);\"><span style=\"color: rgb(255, 0, 0); text-wrap: wrap;\">非常重要：不要直接分享聚推客推广海报，需要先通过聚推客推广海报认证推广员后从去哪儿网页面分享自己的推广海报</span></span></p><p><span style=\"color: rgb(255, 0, 0);\"><span style=\"color: rgb(255, 0, 0); text-wrap: wrap;\"><br/></span></span></p><p><span style=\"color: rgb(255, 0, 0);\"><span style=\"color: rgb(255, 0, 0); text-wrap: wrap;\"><span style=\"color: rgb(255, 0, 0); text-wrap: wrap;\">非常重要：不要直接分享聚推客推广海报，需要先通过聚推客推广海报认证推广员后从去哪儿网页面分享自己的推广海报</span></span></span></p>', '实时结算', '2023-11-28', '2025-12-31', 1744732745, 1744732745);
